import { useEffect, useState } from "react";
import { ethers } from "ethers";
import { InfoContractABI } from "../abi";

export default function EthersProvider() {
  const contractAddress = "0xCa53B795539f85E5Be504d2b5d2df0A7d5F39AB6"
  const gradientBtn =
    "bg-gradient-to-r from-indigo-500 via-blue-500 to-purple-500 text-white rounded-md px-4 py-2 font-semibold shadow transition-all duration-200 hover:scale-105 hover:shadow-lg";
  const gradientBtnLight =
    "bg-gradient-to-r from-indigo-100 via-blue-100 to-purple-100 text-indigo-700 rounded-md px-4 py-2 font-semibold shadow transition-all duration-200 hover:scale-105 hover:shadow-lg";

  const [account, setAccount] = useState("");
  const [contract, setContract] = useState(null);
  const [message, setMessage] = useState("ËØ∑ÂÖàËøûÊé•Èí±ÂåÖ");
  const [hiMessage, setHiMessage] = useState("");
  const [setInfoMessage, setSetInfoMessage] = useState("");
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [getInfoMessage, setGetInfoMessage] = useState("");
  const [listenMessage, setListenMessage] = useState("");
  const [startListenStatus, setStartListenStatus] = useState(false);
  let listener = null;

  function checkWallet() {
    if (typeof window.ethereum === 'undefined') {
      setMessage("ËØ∑ÂÆâË£ÖMetaMaskÈí±ÂåÖ");
      return false;
    } else {
      return true;
    }
  }

  // ËøûÊé•Èí±ÂåÖ
  async function connectWallet() {
    if (!checkWallet()) {
      return;
    }

    try {
      // Ëé∑ÂèñÁî®Êà∑Ë¥¶Êà∑
      const accounts = await window.ethereum.request({
        method: 'eth_requestAccounts'
      });

      if (accounts.length === 0) {
        setMessage("ËØ∑Âú®MetaMask‰∏≠ÊéàÊùÉ");
        return;
      }
      setAccount(accounts[0]);
      setMessage("Èí±ÂåÖËøûÊé•ÊàêÂäü");

      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      // Ëé∑ÂèñÂêàÁ∫¶ÂÆû‰æã
      const contract = new ethers.Contract(contractAddress, InfoContractABI, signer);
      setContract(contract);
    } catch (error) {
      console.error('ËøûÊé•Èí±ÂåÖÂ§±Ë¥•:', error);
      setMessage("ËøûÊé•Èí±ÂåÖÂ§±Ë¥•");
    }
  }

  // Êñ≠ÂºÄÈí±ÂåÖ
  function disconnectWallet() {
    setContract(null)

    if (listener) {
      contract.removeListener("Instructor", listener)
      listener = null
    }

    setAccount("")
    setMessage("Èí±ÂåÖÊñ≠ÂºÄËøûÊé•")
  }

  // Ê£ÄÊü•Èí±ÂåÖ
  useEffect(() => {
    checkWallet();
  }, [])

  // Ë∞ÉÁî® sayHi()
  async function sayHi() {
    if (!contract) {
      setMessage("ËØ∑ÂÖàËøûÊé•Èí±ÂåÖ");
      return;
    }

    try {
      const result = await contract.sayHi();
      setHiMessage(`Ë∞ÉÁî®ÊàêÂäü: ${result}`);
    } catch (error) {
      console.error('Ë∞ÉÁî®Â§±Ë¥•:', error);
      setHiMessage(`Ë∞ÉÁî®Â§±Ë¥•: ${error.message}`);
    }
  }

  // ËÆæÁΩÆ‰ø°ÊÅØ
  async function setInfo() {
    if (!contract) {
      setMessage("ËØ∑ÂÖàËøûÊé•Èí±ÂåÖ");
      return;
    }

    try {
      setSetInfoMessage("ËÆæÁΩÆ‰ø°ÊÅØ‰∏≠...");
      const tx = await contract.setInfo(name, age);
      const result = await tx.wait();
      setSetInfoMessage(`ËÆæÁΩÆ‰ø°ÊÅØÊàêÂäüÔºåÂå∫ÂùóÂè∑: ${result.blockNumber}`);
    } catch (error) {
      console.error('ËÆæÁΩÆ‰ø°ÊÅØÂ§±Ë¥•:', error);
      setMessage(`ËÆæÁΩÆ‰ø°ÊÅØÂ§±Ë¥•: ${error.message}`);
    }
  }

  async function getCurInfo() {
    if (!contract) {
      setMessage("ËØ∑ÂÖàËøûÊé•Èí±ÂåÖ");
      return;
    }

    try {
      const result = await contract.getInfo();
      setGetInfoMessage(`Ëé∑Âèñ‰ø°ÊÅØÊàêÂäü: ${result}`);
    } catch (error) {
      console.error('Ëé∑Âèñ‰ø°ÊÅØÂ§±Ë¥•:', error);
      setGetInfoMessage(`Ëé∑Âèñ‰ø°ÊÅØÂ§±Ë¥•: ${error.message}`);
    }
  }

  function startListen() {
    if (!contract) {
      setMessage("ËØ∑ÂÖàËøûÊé•Èí±ÂåÖ");
      return;
    }

    if (listener) {
      return
    }

    listener = (...args) => {
      const [name, age] = args.slice(0, -1); // ÂÖ∂‰ªñÂèÇÊï∞ÊòØ‰∫ã‰ª∂Êï∞ÊçÆ
      const timestamp = new Date().toLocaleString()
      const message = `[${timestamp}] ${name} ËÆæÁΩÆ‰∫ÜÂπ¥ÈæÑ‰∏∫ ${age}`
      setListenMessage(message);
    }

    contract.on("Instructor", listener);
    setStartListenStatus(true);
  }

  function stopListen() {
    if (!contract) {
      setMessage("ËØ∑ÂÖàËøûÊé•Èí±ÂåÖ");
      return;
    }

    contract.removeListener("Instructor", listener)
    setStartListenStatus(false)
  }


  return (
    <div className="max-w-2xl mx-auto py-10 px-2">
      {/* Èí±ÂåÖËøûÊé•Âç°Áâá */}
      <div className="bg-gray-50 border border-gray-200 rounded-2xl p-6 mb-8 shadow-sm">
        <div className="flex items-center gap-2 mb-4 text-lg font-bold text-gray-700">
          <span className="text-xl">üîó</span> Èí±ÂåÖËøûÊé•
        </div>
        {
          account ?
