import { useState, useEffect } from 'react';
import { InfoContractABI } from "./abi"

function ConnectWallet() {
  const [account, setAccount] = useState('');
  const [balance, setBalance] = useState(0);

  // 连接钱包的函数（由按钮触发）
  const connectWallet = async () => {
    if (typeof window.ethereum === 'undefined') {
      alert('请安装 MetaMask！');
      return;
    }

    try {
      const accounts = await window.ethereum.request({
        method: 'eth_requestAccounts'
      });
      setAccount(accounts[0]);

      // 查询余额
      const balanceHex = await window.ethereum.request({
        method: 'eth_getBalance',
        params: [accounts[0], 'latest']
      });
      const balanceWei = parseInt(balanceHex, 16)
      setBalance(balanceWei / 1e18)
    } catch (error) {
      console.error('用户拒绝授权:', error);
    }
  };

  // 监听账户变化
  useEffect(() => {
    if (typeof window.ethereum === 'undefined') return;

    const handleAccountsChanged = (accounts) => {
      if (accounts.length > 0) {
        setAccount(accounts[0]);
      } else {
        setAccount(''); // 用户断开连接
      }
    };

    window.ethereum.on('accountsChanged', handleAccountsChanged);

    return () => {
      window.ethereum.removeListener('accountsChanged', handleAccountsChanged);
    };
  }, []);

  return (
    <div style={{
      minHeight: '100vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      background: 'linear-gradient(135deg, #f8fafc 0%, #e0e7ff 100%)'
    }}>
      <div style={{
        background: '#fff',
        padding: '2rem 2.5rem',
        borderRadius: '16px',
        boxShadow: '0 4px 24px rgba(0,0,0,0.08)',
        minWidth: '320px',
        textAlign: 'center'
      }}>
        <button
          onClick={connectWallet}
          style={{
            background: account ? '#6366f1' : '#2563eb',
            color: '#fff',
            border: 'none',
            borderRadius: '8px',
            padding: '0.75rem 1.5rem',
            fontSize: '1rem',
            cursor: 'pointer',
            marginBottom: '1.5rem',
            transition: 'background 0.2s'
          }}
          onMouseOver={e => e.target.style.background = '#4f46e5'}
          onMouseOut={e => e.target.style.background = account ? '#6366f1' : '#2563eb'}
        >
          {account ? `已连接: ${account.slice(0, 6)}...${account.slice(-4)}` : '连接钱包'}
        </button>
        <p style={{
          fontSize: '1.1rem',
          color: '#374151',
          margin: 0
        }}>
          余额: <span style={{ fontWeight: 'bold', color: '#2563eb' }}>{balance.toFixed(4)} ETH</span>
        </p>
      </div>
    </div>
  );
}

export default ConnectWallet;
