import { useEffect, useState } from "react";
import { ethers } from "ethers";

export default function EthersProvider() {
  const gradientBtn =
    "bg-gradient-to-r from-indigo-500 via-blue-500 to-purple-500 text-white rounded-md px-4 py-2 font-semibold shadow transition-all duration-200 hover:scale-105 hover:shadow-lg";
  const gradientBtnLight =
    "bg-gradient-to-r from-indigo-100 via-blue-100 to-purple-100 text-indigo-700 rounded-md px-4 py-2 font-semibold shadow transition-all duration-200 hover:scale-105 hover:shadow-lg";

  const [provider, setProvider] = useState(null);

  useEffect(() => { 
    const initProvider = async () => {
      const provider = new ethers.BrowserProvider(window.ethereum);
      setProvider(provider);
    }
    initProvider();
  })


  return (
    <div className="max-w-2xl mx-auto py-10 px-2">
      {/* 钱包连接卡片 */}
      <div className="bg-gray-50 border border-gray-200 rounded-2xl p-6 mb-8 shadow-sm">
        <div className="flex items-center gap-2 mb-4 text-lg font-bold text-gray-700">
          <span className="text-xl">🔗</span> 钱包连接
        </div>
        <button className={gradientBtn + " mb-3"}>断开连接</button>
        <div className="mb-2 text-sm text-gray-800 font-semibold">已连接账户：<span className="font-mono">0x...edde8</span></div>
        <div className="bg-green-200 text-green-800 rounded-md px-4 py-2 text-sm font-medium">钱包连接成功！</div>
      </div>

      {/* Say Hi 测试卡片 */}
      <div className="bg-white border border-gray-200 rounded-2xl p-6 mb-8 shadow-sm">
        <div className="flex items-center gap-2 mb-4 text-lg font-bold text-gray-700">
          <span className="text-xl">👋</span> Say Hi 测试
        </div>
        <button className={gradientBtn}>调用 sayHi()</button>
      </div>

      {/* 设置信息卡片 */}
      <div className="bg-white border border-gray-200 rounded-2xl p-6 mb-8 shadow-sm">
        <div className="flex items-center gap-2 mb-4 text-lg font-bold text-gray-700">
          <span className="text-xl">📝</span> 设置信息
        </div>
        <div className="flex gap-2 mb-4">
          <input className="flex-1 border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-indigo-200" placeholder="输入姓名" />
          <input className="flex-1 border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-indigo-200" placeholder="输入年龄" />
          <button className={gradientBtn}>设置信息</button>
        </div>
      </div>

      {/* 获取信息卡片 */}
      <div className="bg-white border border-gray-200 rounded-2xl p-6 mb-8 shadow-sm">
        <div className="flex items-center gap-2 mb-4 text-lg font-bold text-gray-700">
          <span className="text-xl">📋</span> 获取信息
        </div>
        <button className={gradientBtn}>获取当前信息</button>
      </div>

      {/* 事件监听卡片 */}
      <div className="bg-white border border-gray-200 rounded-2xl p-6 shadow-sm">
        <div className="flex items-center gap-2 mb-4 text-lg font-bold text-gray-700">
          <span className="text-xl">🦻</span> 事件监听
        </div>
        <div className="flex gap-2">
          <button className={gradientBtn}>开始监听事件</button>
          <button className={gradientBtnLight}>停止监听</button>
        </div>
      </div>
    </div>
  );
}
